<template>
        <f7-page>
            
    <f7-navbar>
        <f7-nav-left>
            <f7-link @click="$f7.panel.open();">
                <f7-icon f7="bars"></f7-icon>
            </f7-link>
            
        </f7-nav-left>
        <f7-nav-title>Dashboard</f7-nav-title>
        <f7-nav-right>
            
        </f7-nav-right>
    </f7-navbar>
    <f7-block inner>
      <f7-row tag="p">
        <f7-col>
          <f7-button fill @click="uploadFile">Subir Archivo</f7-button>
        </f7-col>
      </f7-row>
      <f7-row tag="p">
        <f7-col>
          <f7-button fill @click="downloadFile">Descargar Archivo</f7-button>
          <div id="tomar_foto" class="center"
             style="text-align: center; align-items: center; justify-content: center;">
            <img :src="imagenTomada" class="image-section" 
                     style="margin: 0px auto">
          </div>
        </f7-col>
      </f7-row>
      <f7-row tag="p">
        <f7-col>
          <f7-button fill @click="deleteFile">Eliminar Archivo</f7-button>
        </f7-col>
      </f7-row>
      <f7-row tag="p">
        <f7-col>
          <f7-button fill @click="compartir">Compartir!!!</f7-button>
        </f7-col>
      </f7-row>
      <f7-row tag="p">
        <f7-col>
          <f7-button fill @click="compartir2">Compartir2!!!</f7-button>
        </f7-col>
      </f7-row>
    </f7-block>
  </f7-page>
</template>
<script>
import {auth, database, storage} from 'firebase';
import {mapActions} from 'vuex';
export default {
  name: 'Home',
  data() {
    return {
      title: 'Hello World',
      panel_left: false,
      imagenTomada: null,
      db:null
    };
  },
  mounted(){
    console.log("Iniciando")
    let usuario = auth().currentUser;
    console.log("Dashboard Usuario",usuario);
    auth().onAuthStateChanged((user)=>{
      if(user){
        console.log("onAuthStateChange", user)
        this.saveUsuario(user.uid)
        this.cargarFire()
      }
    });
    if(window.plugins.OneSignal){
      window.plugins.OneSignal.setSubscription(true);
    }
  },
  methods:{
    ...mapActions(['saveUsuario']),
    compartir(){
      console.log("Compartirlo");
      navigator.plugins.socialsharing.share('Message only');
    },
    compartir2(){
      console.log("Compartirlo");
      window.plugins.socialsharing.share('Message only');
    },
    deleteFile(){
      console.log("Eliminando archivo");
      let storageRef = storage().ref();
      var desertRef = storageRef.child('mountains.jpg');

      // Delete the file
      desertRef.delete().then(function() {
        console.log("Archivo Eliminado")
      }).catch(function(error) {
        console.log("Error al eliminar archivo", error)
        // Uh-oh, an error occurred!
      });
    },
    downloadFile(){
      console.log("Descargando archivo");
      let storageRef = storage().ref();
      // Create a reference to the file we want to download
      var starsRef = storageRef.child('mountains.jpg');

      // Get the download URL
      starsRef.getDownloadURL().then((url)=> {
        console.log("Archivo Descargado")
        console.log(url);
        this.imagenTomada = url;

        console.log("Solicitando Descarga")
        var xhr = new XMLHttpRequest();
        xhr.responseType = 'blob';
        xhr.onload = function(event) {
          console.log("Archivo Blod")
          console.log(xhr.response)
          var blob = xhr.response;
        };
        xhr.open('GET', url);
        xhr.send()
        // Insert url into an <img> tag to "download"
      }).catch(function(error) {

        // A full list of error codes is available at
        // https://firebase.google.com/docs/storage/web/handle-errors
        switch (error.code) {
          case 'storage/object_not_found':
            // File doesn't exist
            break;

          case 'storage/unauthorized':
            // User doesn't have permission to access the object
            break;

          case 'storage/canceled':
            // User canceled the upload
            break;

          case 'storage/unknown':
            // Unknown error occurred, inspect the server response
            break;
        }
      });
    },
    uploadFile(){
      console.log("Subiendo Archivo");
      let storageRef = storage().ref();
      var mountainsRef = storageRef.child('mountains.jpg');
      var message = 'data:image/png;base64,';
      var uploadTask = mountainsRef.putString(message, 'data_url');

      uploadTask.on('state_changed', function(snapshot){
        // Observe state change events such as progress, pause, and resume
        // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
        var progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log('Upload is ' + progress + '% done');
        switch (snapshot.state) {
          case storage.TaskState.PAUSED: // or 'paused'
            console.log('Upload is paused');
            break;
          case storage.TaskState.RUNNING: // or 'running'
            console.log('Upload is running');
            break;
        }
      }, function(error) {
        // Handle unsuccessful uploads
      }, function() {
        // Handle successful uploads on complete
        // For instance, get the download URL: https://firebasestorage.googleapis.com/...
        uploadTask.snapshot.ref.getDownloadURL().then(function(downloadURL) {
          console.log('File available at', downloadURL);
        });
      });

    },
    cargarFire(){
      let usuario = auth().currentUser;
      this.db= database();
      console.log("Perfil Usuario", usuario);
    },
    
  },
  created() {
  }
};
</script>